(2032,'TRIGGER STMAST ALL','0000-00-00','\r\n\r\nDELIMITER $$\r\n\r\n/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `TRG_INS_STMAST` */$$\r\n\r\n/*!50003 CREATE */ /*!50017 DEFINER = \'root\'@\'localhost\' */ /*!50003 TRIGGER `TRG_INS_STMAST` BEFORE INSERT ON `stmast` FOR EACH ROW BEGIN \r\n\r\n   IF LEFT(USER(),5)<>\'kasir\' OR NEW.ADDID =\'\' THEN \r\n\r\n       SET NEW.ADDID = USER(); \r\n\r\n       SET NEW.UPDID = USER(); \r\n\r\n   Else \r\n\r\n       SET NEW.UPDID = NEW.ADDID; \r\n\r\n   END IF; \r\n\r\n   SET NEW.ADDTIME = SYSDATE(); \r\n\r\n   SET NEW.UPDTIME = SYSDATE(); \r\n\r\nEND */$$\r\n\r\n\r\nDELIMITER ;\r\n\r\n\r\nDELIMITER $$\r\n\r\n/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `TRG_UPD_STMAST` */$$\r\n\r\n/*!50003 CREATE */ /*!50017 DEFINER = \'root\'@\'localhost\' */ /*!50003 TRIGGER `TRG_UPD_STMAST` BEFORE UPDATE ON `stmast` FOR EACH ROW BEGIN\r\n\r\n 	DECLARE n INTEGER;\r\n\r\n 	SELECT COUNT(*) INTO n FROM stockol WHERE PRDCD=NEW.PRDCD;\r\n\r\n 	IF n = 0 THEN\r\n\r\n 		INSERT INTO stockol(\r\n\r\n 			PRDCD\r\n\r\n 			,BEGBAL\r\n\r\n 			,BEGBAL_DATE\r\n\r\n 			,QTY\r\n\r\n 			,SALES\r\n\r\n 			,RETUR\r\n\r\n 			,TRFIN\r\n\r\n 			,TRFOUT\r\n\r\n 			,ADJ\r\n\r\n 			,KONS\r\n\r\n 			,SPD\r\n\r\n 			,UPDID\r\n\r\n 			,UPDTIME\r\n\r\n 		)VALUES(\r\n\r\n 			NEW.PRDCD\r\n\r\n 			,NEW.BEGBAL\r\n\r\n 			,NEW.UPDTIME\r\n\r\n 			,NEW.BEGBAL-NEW.SALES+NEW.RETUR+NEW.TRFIN-NEW.TRFOUT+NEW.ADJ+NEW.BA+NEW.BS\r\n\r\n 			,NEW.SALES\r\n\r\n 			,NEW.RETUR\r\n\r\n 			,NEW.TRFIN\r\n\r\n 			,NEW.TRFOUT\r\n\r\n 			,NEW.ADJ\r\n\r\n 			,NEW.KONS\r\n\r\n 			,NEW.SPD\r\n\r\n 			,\'N\'\r\n\r\n 			,NOW()\r\n\r\n 		);\r\n\r\n 		INSERT INTO stockol_id(\r\n\r\n 			PRDCD\r\n\r\n 			,BEGBAL\r\n\r\n 			,BEGBAL_DATE\r\n\r\n 			,QTY\r\n\r\n 			,SALES\r\n\r\n 			,RETUR\r\n\r\n 			,TRFIN\r\n\r\n 			,TRFOUT\r\n\r\n 			,ADJ\r\n\r\n 			,KONS\r\n\r\n 			,SPD\r\n\r\n 			,UPDID\r\n\r\n 			,UPDTIME\r\n\r\n 		)VALUES(\r\n\r\n 			NEW.PRDCD\r\n\r\n 			,NEW.BEGBAL\r\n\r\n 			,NEW.UPDTIME\r\n\r\n 			,NEW.BEGBAL-NEW.SALES+NEW.RETUR+NEW.TRFIN-NEW.TRFOUT+NEW.ADJ+NEW.BA+NEW.BS\r\n\r\n 			,NEW.SALES\r\n\r\n 			,NEW.RETUR\r\n\r\n 			,NEW.TRFIN\r\n\r\n 			,NEW.TRFOUT\r\n\r\n 			,NEW.ADJ\r\n\r\n 			,NEW.KONS\r\n\r\n 			,NEW.SPD\r\n\r\n 			,\'N\'\r\n\r\n 			,NOW()\r\n\r\n 		);\r\n\r\n 	ELSE\r\n\r\n        IF NEW.BEGBAL <> OLD.BEGBAL OR NEW.SALES <> OLD.SALES OR NEW.RETUR <> OLD.RETUR OR\r\n\r\n           NEW.TRFIN <> OLD.TRFIN OR NEW.TRFOUT <> OLD.TRFOUT OR NEW.ADJ <> OLD.ADJ OR NEW.SPD <> OLD.SPD THEN\r\n\r\n 		    UPDATE stockol SET\r\n\r\n 			    BEGBAL = NEW.BEGBAL\r\n\r\n 			    ,BEGBAL_DATE = NEW.UPDTIME \r\n\r\n 			    ,QTY = NEW.BEGBAL-NEW.SALES+NEW.RETUR+NEW.TRFIN-NEW.TRFOUT+NEW.ADJ+NEW.BA+NEW.BS\r\n\r\n 			    ,SALES  = NEW.SALES\r\n\r\n 			    ,RETUR = NEW.RETUR\r\n\r\n 			    ,TRFIN = NEW.TRFIN\r\n\r\n 			    ,TRFOUT = NEW.TRFOUT\r\n\r\n 			    ,ADJ = NEW.ADJ\r\n\r\n 			    ,KONS = NEW.KONS\r\n\r\n 			    ,SPD = NEW.SPD\r\n\r\n 			    ,UPDID = \'N\'\r\n\r\n 			    ,UPDTIME = NOW()\r\n\r\n 		    WHERE PRDCD=NEW.PRDCD;\r\n\r\n 		    UPDATE stockol_id SET\r\n\r\n 			    BEGBAL = NEW.BEGBAL\r\n\r\n 			    ,BEGBAL_DATE = NEW.UPDTIME \r\n\r\n 			    ,QTY = NEW.BEGBAL-NEW.SALES+NEW.RETUR+NEW.TRFIN-NEW.TRFOUT+NEW.ADJ+NEW.BA+NEW.BS\r\n\r\n 			    ,SALES  = NEW.SALES\r\n\r\n 			    ,RETUR = NEW.RETUR\r\n\r\n 			    ,TRFIN = NEW.TRFIN\r\n\r\n 			    ,TRFOUT = NEW.TRFOUT\r\n\r\n 			    ,ADJ = NEW.ADJ\r\n\r\n 			    ,KONS = NEW.KONS\r\n\r\n 			    ,SPD = NEW.SPD\r\n\r\n 			    ,UPDID = \'N\'\r\n\r\n 			    ,UPDTIME = NOW()\r\n\r\n 		    WHERE PRDCD=NEW.PRDCD;\r\n\r\n        END IF; 	END IF;\r\n\r\n 	IF LEFT(USER(),5)<>\'kasir\' OR NEW.UPDID=\'\' THEN\r\n\r\n 	   SET NEW.UPDID=USER();\r\n\r\n 	END IF;\r\n\r\n 	SET NEW.UPDTIME=SYSDATE();\r\n\r\n END */$$\r\n\r\n\r\nDELIMITER ;\r\n\r\n\r\n\r\nDELIMITER $$\r\n\r\n/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `STMAST_HIS1` */$$\r\n\r\n/*!50003 CREATE */ /*!50017 DEFINER = \'root\'@\'localhost\' */ /*!