(2040,'Instalasi 3 Layar RO','0000-00-00','1. Tembak komputer anak, tskill pos*\r\n\r\n2. Tembak SQL, jalankan query : \r\n\r\nCREATE TABLE `constro` (\r\n  `RECID` CHAR(1) NOT NULL DEFAULT \'\',\r\n  `RKEY` CHAR(3) NOT NULL DEFAULT \'\',\r\n  `DESC` VARCHAR(84) DEFAULT NULL,\r\n  `DOCNO` DECIMAL(6,0) DEFAULT \'0\',\r\n  `RDOCNO` DECIMAL(6,0) DEFAULT \'0\',\r\n  `PERIOD` DATE DEFAULT NULL,\r\n  `PERIOD1` DATE DEFAULT NULL,\r\n  `PERIOD2` DATE DEFAULT NULL,\r\n  `JENIS` CHAR(1) DEFAULT NULL,\r\n  `ADDID` VARCHAR(45) DEFAULT NULL,\r\n  `ADDTIME` DATETIME DEFAULT NULL,\r\n  `UPDID` VARCHAR(45) DEFAULT NULL,\r\n  `UPDTIME` DATETIME DEFAULT NULL\r\n) ENGINE=INNODB DEFAULT CHARSET=latin1;\r\n\r\n3. Instal MSChart.exe\r\n\r\n4. Instal Setup.exe\r\n\r\n5. Copy IDM.Classcon.dll dan IDM.Classcon.pdb ke d:\\backoff\\\r\n\r\n6. Copy RO_Lite.exe dan RO_Lite.pdb ke d:\\backoff\\\r\n\r\n7. Jalankan Query\r\n\r\nDROP TABLE IF EXISTS `eventdetail`;\r\nCREATE TABLE `eventdetail` (\r\n  `RECID` char(1) DEFAULT NULL,\r\n  `KODE_EVENT_TOKO` int(8) NOT NULL DEFAULT \'0\',\r\n  `KODE_EVENT` char(2) DEFAULT NULL,\r\n  `NAMA_EVENT` char(30) DEFAULT NULL,\r\n  `MULAI_HITUNG_PKM` decimal(2,0) DEFAULT NULL,\r\n  `AKHIR_HITUNG_PKM` decimal(2,0) DEFAULT NULL,\r\n  `TANGGAL_EVENT` date NOT NULL DEFAULT \'0000-00-00\',\r\n  `ADDTIME` datetime DEFAULT NULL,\r\n  `ALASAN_BATAL` char(200) DEFAULT NULL,\r\n  `TANGGAL_BATAL` date DEFAULT NULL,\r\n  PRIMARY KEY (`KODE_EVENT_TOKO`,`TANGGAL_EVENT`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;\r\n\r\nDROP TABLE IF EXISTS `eventdetail_barang`;\r\nCREATE TABLE `eventdetail_barang` (\r\n  `RECID` char(1) DEFAULT NULL,\r\n  `KODE_EVENT_TOKO` int(8) NOT NULL DEFAULT \'0\',\r\n  `KODE_EVENT` char(2) DEFAULT NULL,\r\n  `TANGGAL_EVENT` date NOT NULL DEFAULT \'0000-00-00\',\r\n  `PRDCD` varchar(8) NOT NULL DEFAULT \'\',\r\n  `NILAI_PKM` decimal(12,0) DEFAULT NULL,\r\n  `PKM_AWAL` decimal(12,0) DEFAULT NULL,\r\n  `SPD` decimal(9,0) DEFAULT NULL,\r\n  `TRANS_EVN` char(1) DEFAULT NULL,\r\n  PRIMARY KEY (`KODE_EVENT_TOKO`,`TANGGAL_EVENT`,`PRDCD`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;\r\n\r\nSELECT * FROM constro;\r\nINSERT INTO `constro` VALUES (\'\',\'PRO\',\'G137\',\'2\',\'0\',\'2020-01-23\',\'2020-01-23\',NULL,NULL,NULL,\'2019-04-30 06:40:22\',NULL,\'2019-04-30 06:40:22\');\r\nINSERT INTO const SELECT * FROM constro;\r\nSELECT * FROM const WHERE rkey=\'pro\'; \r\n\r\n\r\nNote : jika ada error promo_gab, rename promo_gab jadi bck_promo_gab\r\n                                '),
