(109,'Lihat Status Istore','SINTAK LIHAT STATUS ISTORE\r\n-----------------------------------------------------------------------------------------------------------------------------------------------------\r\n-Cek Kode Pesanan Belum Update;\r\nSELECT l2.kode FROM\r\n (SELECT l.Kode, l.Status, l.Deskripsi, l.Keterangan FROM idelivery_log_status_pesanan l  \r\nWHERE l.status NOT IN (\'1.2.2.3\',\'1.2.3.3\',\'1.2.3.2\') AND l.kode NOT IN \r\n(SELECT kode FROM idelivery_log_status_pesanan l1 \r\nWHERE l1.kode = l1.kode AND l1.status > l.status)) \r\nl2 INNER JOIN\r\n (SELECT Kode, Referensi, Tanggal_Pesanan, ADDTIME, Tanggal_Kirim_Min, Tanggal_Kirim_Max  \r\nFROM idelivery_pesanan \r\nWHERE Referensi IN (\'DU\') AND Sumber = \'ISTORE\'  AND CURDATE() > Tanggal_Kirim_Max)\r\n p ON l2.Kode = p.Kode;\r\n\r\n-Cek Kode Pesanan Belum Update status cansel;\r\nSELECT l2.kode FROM \r\n(SELECT l.Kode, l.Status, l.Deskripsi, l.Keterangan FROM idelivery_log_status_pesanan l  \r\nWHERE l.status NOT IN (\'1.2.2.3\',\'1.2.3.3\',\'1.2.3.2\') AND l.kode NOT IN \r\n(SELECT kode FROM idelivery_log_status_pesanan l1 \r\nWHERE l1.kode = l1.kode AND l1.status > l.status)) l2 INNER JOIN\r\n (SELECT Kode, Referensi, Tanggal_Pesanan, ADDTIME, Tanggal_Kirim_Min, Tanggal_Kirim_Max  \r\nFROM idelivery_pesanan \r\nWHERE Referensi IN (\'DU\') AND Sumber = \'ISTORE\'  \r\nAND CURDATE() > Tanggal_Kirim_Max AND keterangan != \'CANCEL\') p ON l2.Kode = p.Kode;\r\n \r\n- Cek Kode Pesanan Apakah Sudah pernah struk/belum;\r\nSELECT * FROM mtran WHERE customer in ( Diisi sesuai kode pesanan );\r\n \r\n- Cek Kode Pesanan yang masuk;\r\nSELECT * FROM idelivery_pesanan WHERE kode IN ( Diisi sesuai kode pesanan );\r\n\r\n- Cek Kode Pesanan Di log Status pesanan;\r\nSELECT * FROM idelivery_log_status_pesanan WHERE kode in ( Diisi sesuai kode pesanan ); \r\nCopy yang sudah clear;\r\nSELECT kode,`status`,deskripsi,keterangan,kirim_server FROM idelivery_log_status_pesanan \r\nWHERE kode in ( Diisi sesuai kode pesanan );\r\n\r\n'),
