(421,'DATA HILANG','\r\nEKSTRAK FILE DT WT BYR DAN HARIAN.CSV DARI DATA HARIAN\r\n\r\nJIKA DATA YANG HILANG SATU BULAN,COBA EKSTRAK DATA HARIAN DARI 208 SECARA BERSAMAAN(PKUNZIP HR1908*.* DT*.*  WT*.* \r\n\r\n\r\n\r\nambil data harian 1 bulan  di  W(jika sudah sharing)\r\n\r\nlalu buka cmd, copy hr1909*.1il  D:\\T1IL\r\n\r\npkunzip hr1909*.* dt0*.*\r\n\r\ncopy dt0*.* mtran_cad.csv\r\n\r\n(di cek dlu csv nya sudah ada isinya blm)\r\n\r\njangan lupa buat table mtran di sql toko nya\r\n\r\nlalu tinggal jalanin  langkah di bawah \r\n\r\n\r\n\r\n***** Insert Data mtran dari data Dt harian toko  ******\r\n\r\n\r\n\r\nLOAD DATA LOCAL INFILE \"D://T1IL//mtran_cad.csv\" INTO table MTRAN_CAD FIELDS TERMINATED BY \'|\' IGNORE 1 LINES (@recid,@station,\r\n\r\n@shift,@rtype,@docno,@seqno,@div,@prdcd,@qty,@price,@gross,@discount,@shop,@tanggal,@jam,\r\n\r\n@kons,@ttype,@hpp,@promosi,@ppn,@ptag,@cat_cod,@nm_vcr,@no_vcr,@bkp,@sup_bkp,@plumd)\r\n\r\nSet recid=@recid,lokasi=\'01\',station=@station,shift=@shift,rtype=@rtype,docno=@docno,seqno=@seqno,PLU=@prdcd,plu_nas=@plumd,\r\n\r\n    qty=@qty,price=@price,gross=@gross,discount=@discount,shop=@shop,tanggal=str_to_date(@tanggal,\'%d-%m-%Y\'),tglshift=str_to_date(@tanggal,\'%d-%m-%Y\'),\r\n\r\n    jam=@jam,hpp=@hpp,KDPRomo=@promosi,ppn=@ppn,ptag=@ptag,catcode=@cat_cod,no_bon=@no_vcr,bkp=if(@bkp=\'T\',\'Y\',\'N\'),sub_bkp=@sup_bkp,customer=\' \',\r\n\r\n    kode_nip=\' \',jns_bayar=@ttype;\r\n\r\n\r\n\r\n\r\n\r\n***** Insert Data Mstran dari Wt harian toko ******\r\n\r\n\r\n\r\nLOAD DATA LOCAL INFILE \"C://TQXP/WT0804T.QXP\" INTO TABLE MSTRAN_CAD FIELDS TERMINATED BY \'|\' IGNORE 1 LINES\r\n\r\n(@recid,@rtype,@docno,@seqno,@div,@prdcd,@qty,@price,@gross,@cterm,@docno2,@istype,@invno,@toko,@date,\r\n\r\n@date2,@keterangan,@ptag,@cat_cod,@lokasi,@tgl1,@tgl2,@ppn,@toko_1,@date3,@docno3,@shop,@price_idm,\r\n\r\n@ppnbm_idm,@ppnrp_idm,@lt,@rak,@bar,@bkp,@PKP,@sub_bkp,@plumd,@gross_jual,@PRICE_JUAL,@seqno,@totseqno,@nik1,@nik2,@nik3)\r\n\r\nSET recid=@recid,gudang=IF(@toko=\'G137\',\'G137\',IF(@toko=\'G061\',\'G061\',IF(@toko=\'G067\',\'G067\',IF(@toko=\'G051\',\'G051\',IF(LEFT(@toko,1)=\'T\' AND @rtype<>\'X\',@toko,\'\'))))),lokasi=@lokasi,rtype=IF(@rtype=\'B\',\'BPB\',IF(@rtype=\'K\',\'K\',IF(@rtype=\'I\',\'I\',IF(@rtype=\'O\',\'O\',\'X\')))),\r\n\r\n    bukti_no=@docno,bukti_tgl=STR_TO_DATE(@tgl1,\'%d-%m-%Y\'),supco=IF(LEFT(@toko,1)=\'S\',@toko,NULL),\r\n\r\n    prdcd=@prdcd,plu_nas=@plumd,qty=@qty,price=@price,gross=@gross,bonus=0,ppn=@ppn,fmdfee=0,ppnfee=0,\r\n\r\n    ppnbm=0,hrg_botol=0,disc1=0,disc2=0,disc3=0,disc4cr=0,disc4rr=0,disc4jr=0,invno=IF(@istype=\'V\',@DOCNO2,@docno2),\r\n\r\n    inv_date=STR_TO_DATE(@tgl2,\'%d-%m-%Y\'),po_no=@invno,po_date=NULL,Istype=@istype,keter=@keterangan,\r\n\r\n    lt=@lt,rak=@rak,bar=@bar,bkp=IF(bkp=\'T\',\'Y\',\'N\'),sub_bkp=@sub_bkp,gross_jual=@gross_jual;\r\n\r\n\r\n\r\nJIKA SUDAH BERHASIL LOAD INFILE JALANKAN UPDATE MSTRAN_cad DI BAWAH INI\r\nupdate mstran_cad set price_jual=gross_jual;\r\nupdate mstran_cad set gross_jual=plu_nas;\r\nupdate mstran_cad set plu_nas=prdcd;\r\n\r\n\r\n\r\n***** Insert Data Bayar dari Byr harian toko *****\r\n\r\n\r\n\r\nLOAD DATA LOCAL INFILE \"C://TQXP//BYR19080.QXP\" INTO table BAYAR_CAD FIELDS TERMINATED BY \'|\' IGNORE 1 LINES\r\n\r\n(@RECID,@TANGGAL,@TGLSHIFT,@SHIFT,@STATION,@DOCNO,@TIPE,@KDEDC,@KDKARTU,@NOMOR,@NOACC,@kdbin,\r\n\r\n@NILAI,@PEDULI,@CHARGE,@TUNAI,@TrxId,@NOD,@TRXIDT,@NON,@TYPETRAN,@TERMINAL,@MERCHID,@FTIME,\r\n\r\n@STAN,@APPROV,@BATCHNO,@PAN,@ADDID,@ADDTIME,@UPDID,@UPDTIME,@rowid,@TOTAL,@PPN,@KEMBALI,\r\n\r\n@RTYPE,@SLS_SPR,@DSR_DISC,@SLS_SPQ,@DSR_DISQ,@NO_BON,@Tenor_Cicilan,@MDR)\r\n\r\nSet RECID=@RECID,TANGGAL=@TANGGAL,TGLSHIFT=@TGLSHIFT,SHIFT=@SHIFT,STATION=@STATION,DOCNO=@DOCNO,\r\n\r\nTIPE=@TIPE,KDEDC=@KDEDC,KDKARTU=@KDKARTU,NOMOR=@NOMOR,NOACC=@NOACC,kdbin=@kdbin,NILAI=@NILAI,\r\n\r\nPEDULI=@PEDULI,CHARGE=@CHARGE,TUNAI=@TUNAI,TrxId=@TrxId,NOD=@NOD,TRXIDT=@TRXIDT,NON=@NON,\r\n\r\nTYPETRAN=@TYPETRAN,TERMINAL=@TERMINAL,MERCHID=@MERCHID,FTIME=@FTIME,STAN=@STAN,APPROV=@APPROV,\r\n\r\nBATCHNO=@BATCHNO,PAN=@PAN,ADDID=@ADDID,ADDTIME=@ADDTIME,UPDID=@UPDID,UPDTIME=@UPDTIME,\r\n\r\nrowid=@rowid,TOTAL=@TOTAL,PPN=@PPN,KEMBALI=@KEMBALI,RTYPE=@RTYPE,SLS_SPR=@SLS_SPR,DSR_DISC=@DSR_DISC,\r\n\r\nSLS_SPQ=@SLS_SPQ,DSR_DISQ=@DSR_DISQ,NO_BON=@NO_BON,Tenor_Cicilan=@Tenor_Cicilan,MDR=@MDR;\r\n\r\n\r\n\r\n\r\n\r\nwtran toko take over\r\n\r\n\r\n\r\nLOAD DATA LOCAL INFILE \"D://IDMTO//WTRAN.CSV\" INTO table MST FIELDS TERMINATED BY \',\' IGNORE 1 LINES\r\n\r\n(@RECID,@RTYPE,@DOCNO,@SEQNO,@DIV,@PRDCD,@QTY,@PRICE,@GROSS,@CTERM,@DOCNO2,@ISTYPE,@INVNO,@TOKO,@DATE,@DATE2\r\n\r\n,@KETERANGAN,@PTAG,@CAT_COD,@LOKASI,@TGL1,@TGL2,@PPN,@TOKO_1,@DATE3,@DOCNO3,@SHOP,@PRICE_IDM,@PPNBM_IDM\r\n\r\n,@PPNRP_IDM,@LT,@RAK,@BAR,@BKP,@SUB_BKP)\r\n\r\nSet recid=@recid,gudang=if(@toko=\'G113\',\'G113\',\'\'),lokasi=@lokasi,rtype=\'BPB\',\r\n\r\n    bukti_no=@docno,bukti_tgl=str_to_date(@tgl1,\'%d-%m-%Y\'),supco=null,\r\n\r\n    prdcd=@prdcd,qty=@qty,price=@price,gross=@gross,bonus=0,ppn=@ppn,fmdfee=0,ppnfee=0,\r\n\r\n    ppnbm=0,hrg_botol=0,disc1=0,disc2=0,disc3=0,disc4cr=0,disc4rr=0,disc4jr=0,invno=@docno2,\r\n\r\n    inv_date=str_to_date(@tgl2,\'%d-%m-%Y\'),po_no=@docno3,po_date=null,Istype=@istype,keter=@keterangan,\r\n\r\n    bkp=if(bkp=\'T\',\'Y\',\'N\'),sub_bkp=@sub_bkp;\r\n\r\n\r\n\r\nUPDATE NOMOR BPB,RETUR DAN ADJ\r\n\r\n-> BPB\r\n\r\nselect distinct BUKTI_NO from mStran where month(BUKTI_TGL)=08 AND RTYPE=\'BPB\' ORDER BY BUKTI_NO ASC;\r\n\r\n(lihat no terakhir berapa update docno di const rtype BPB jadi nomor teakhir +2 )\r\n\r\n\r\n\r\nSELECT * FROM CONST WHERE RKEY=\'BPB\';\r\n\r\n\r\n\r\n-> RETUR\r\n\r\nselect distinct BUKTI_NO from mStran where month(BUKTI_TGL)=08 AND RTYPE=\'K\' ORDER BY BUKTI_NO ASC;\r\n\r\n(lihat no terakhir berapa update docno di const rtype NPB jadi nomor teakhir +2 )\r\n\r\n\r\n\r\nSELECT * FROM CONST WHERE RKEY=\'NPB\';\r\n\r\n\r\n\r\n->ADJ (SO)\r\n\r\nselect distinct BUKTI_NO from mStran where month(BUKTI_TGL)=08 AND RTYPE=\'X\' ORDER BY BUKTI_NO ASC;\r\n\r\n(lihat no terakhir berapa update docno di const rtype NKL jadi nomor teakhir +2 )\r\n\r\nSELECT * FROM CONST WHERE RKEY=\'NKL\';\r\n\r\n\r\n\r\niNSERT TAT\r\n\r\n\r\n\r\nINSERT INTO MSTRAN(GUDANG,LOKASI,RTYPE,BUKTI_NO,BUKTI_TGL,PRDCD,PLU_NAS,QTY,PRICE,GROSS,PPN,INVNO,\r\n\r\nINV_DATE,JAM,ISTYPE,KETER,BKP,SUB_BKP,ADDID,ADDTIME ) SELECT WI.TOKO AS GUDANG,WI.LOKASI,WI.RTYPE ,WI.DOCNO AS BUKTI_NO,NOW(),WI.PRDCD,P.PLUMD,\r\n\r\nWI.QTY,WI.PRICE,WI.GROSS,WI.PPN,WI.INVNO, WI.TGL2 AS INV_DATE,CURTIME(),\r\n\r\nWI.ISTYPE,WI.KETERANGAN AS KETER,WI.BKP,WI.SUB_BKP,WI.ADDID,NOW() FROM WTRAN_IN WI INNER JOIN PRODMAST P ON WI.PRDCD=P.PRDCD AND DOCNO=2  AND INVNO=\'2\';\r\n\r\n\r\n\r\nCREATE TO CSV\r\n\r\nSELECT a,b,a+b INTO OUTFILE \'/tmp/result.txt\'\r\n\r\n  FIELDS TERMINATED BY \',\' OPTIONALLY ENCLOSED BY \'\"\'\r\n\r\n  LINES TERMINATED BY \'\\n\'\r\n\r\n  FROM test_table;\r\n\r\n\r\n\r\n\r\n\r\nInsert initial;\r\n\r\nLOAD DATA LOCAL INFILE \'C://TQXP//harian.csv\' INTO TABLE initial_CAD FIELDS TERMINATED BY \',\' OPTIONALLY ENCLOSED BY \'\\\'\' ESCAPED BY \'\\\'\' LINES TERMINATED BY \'\\n\'\r\n\r\nIGNORE 1 LINES(@Pos_Flag,@Pos_Date,@Station,@Shift,@Time_Start,@Time_End,@Kasir_Name,@Last_No_Sl,@Last_No_Rt,@Cash_Beg,@Cash_Act,@Cash_Ref,@Sales_Cash,@Sales_Ref,@Sales_Cr,@Sales_Cc,@Sales_Vou,@Sales_Cou,@Sales_Db,@SlPlu,@AcPlu,@Potongan,@TtPlu,@PPn,@S_Count,@R_Count,@S_Count2,@R_Count2,@P_Count2,@Tmp,@Vou_Idm,@Kredit_Kop,@Sw_Teras,@S_14601,@R_14601,@P_14601,@Var_Lns,@Vou_Oth,@S_BbsPpn,@P_BbsPpn,@Ambil_Draw,@NIK,@VAR_PLUS)\r\n\r\nSET recid=@Pos_Flag,tanggal=STR_TO_DATE(@Pos_Date,\'%d-%m-%Y\'),station=@Station,shift=@Shift,trn_start=@Time_Start,\r\n\r\ntrn_end=@Time_End,kasir_name=@Kasir_Name,last_struk=@Last_No_Sl,last_retur=@Last_No_Rt,\r\n\r\nkas_awal=@Cash_Beg,kas_aktual=@Cash_Act,kas_coun=@Cash_Ref,kas_comp=@Sales_Cash,ret_comp=@Sales_Ref,\r\n\r\nsales_cr=@Sales_Cr,sales_cc=@Sales_Cc,sales_vou=@Sales_Vou,sales_cou=@Sales_Cou,sales_db=@Sales_Db,\r\n\r\npotongan=@Potongan,ppn=@PPn,s_count=@S_Count,r_count=@R_Count,s_count2=@S_Count2,r_count2=@R_Count2,\r\n\r\np_count2=@P_Count2,vou_idm=@Vou_Idm,kredit_kop=@Kredit_Kop,sw_teras=@Sw_Teras,s_14601=@S_14601,\r\n\r\nr_14601=@R_14601,p_14601=@P_14601,var_lns=@Var_Lns,ambil_draw=@Ambil_Draw,sales_cash=@sales_cash,NIK=@NIK,VAR_PLUS=@VAR_PLUS;\r\n\r\n\r\n\r\ninsert const\r\n\r\n\r\n\r\nload data local infile \'d://WINNER//const.21t\' into table const fields terminated by \',\' optionally enclosed by \'\\\'\' ignore 1 lines(@RECID,@RKEY,@DESC,@DOCNO,@RDOCNO,@PERIOD,@PERIODE1,@PERIODE2,@JENIS)\r\n\r\nset RECID=@RECID,RKEY=@RKEY,`DESC`=@DESC,DOCNO=@DOCNO,RDOCNO=@RDOCNO,PERIOD=STR_TO_DATE(@PERIOD,\'%d-%m-%Y\')\r\n\r\n,PERIOD1=str_to_date(@periode1,\'%d-%m-%Y\'),PERIOD2=str_to_date(@periode2,\'%d-%m-%Y\'),JENIS=@jenis;\r\n\r\n\r\n\r\n\r\n\r\nsintax buat lihat bukti_tgl di mstran\r\n\r\nselect distinct date_format(bukti_tgl,\'%Y-%m-%d\') from mstran where month(bukti_tgl)=02;        '),
